import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';
function EditOrder(){
    
    const location = useLocation();
    console.log('location', location.state);
    //const { selectedGroceries  = [] } = location.state || {}
    const selectedItems = location.state?.selectedItems || [];
    console.log('maybe items?', selectedItems);
    const [groceryDetails, setGroceryDetails] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    

    useEffect(() => {
        if(selectedItems.length > 0 && !loading){
            console.log('stuff', selectedItems);
            const getGroceryInfo = async() => {
                setLoading(true);
                try{
                    console.log('Selected groceries:', selectedItems);
                    const response = await axios.post('http://localhost:8080/api/EditOrder', {
                        selectedItems: selectedItems}, 
                        {headers: {'Content-Type': 'application/json'}
                    });

                    console.log('server response', response.data);
                    setGroceryDetails((prevDetails) => {
                        const mapItems = new Map();
                        prevDetails.forEach(item => {
                            mapItems.set(item.grocery_id, item);
                        })
                        response.data.forEach(newItem => {
                            if (mapItems.has(newItem.grocery_id)) {
                                mapItems.get(newItem.grocery_id).quantity += selectedItems.find(item => item.grocery_id === newItem.grocery_id).quantity;
                            } else {
                                mapItems.set(newItem.grocery_id, { ...newItem, quantity: selectedItems.find(item => item.grocery_id === newItem.grocery_id).quantity })
                            }
                        });
                        return Array.from(mapItems.values());
                    });
                    
                } catch (err){
                    setError('Failed to fetch grocery data, try again');
                    console.error(err.message);
                } finally{
                    setLoading(false);
                }
            };
            getGroceryInfo();
        }
        
    }, [selectedItems]);

    console.log('error', error);
    console.log('loading', loading);

    // if(loading) {
    //     return <div>Loading...</div>;
    // }
    if(error){
        return <div>Error: {error.message}</div>;
    }



    return(
            <div>
           <p>
                Order Number:
           </p>
           <button><Link to='/ShopItems'>Add Item</Link></button>
           <table className='Orders'>
                <thead>
                    <tr>
                        <th>Item</th>
                        <th>Unit cost</th>
                        <th>No. Units</th>
                        <th>Total Cost</th>
                        <th>Added by</th>
                    </tr>
                </thead>
                <tbody>
                    {groceryDetails.map((row, index) => (
                        <tr key={index}>
                            <td>{row.grocery_name}</td>
                            <td>{row.grocery_price}</td>
                            <td>{row.quantity}</td>
                            <td>{row.grocery_price * row.quantity}</td>
                        </tr>
                    ))}
                </tbody>
           </table>

           <br/>
           <table className='Orders'>
                <tr>
                    <th>User</th>
                    <th>Total Cost</th>
                </tr>
           </table>
           <p>
                Total cost:
           </p>
           <label>
                Apply discount
                <select className='discount'>
                    <option value="NoOp">Please Choose an available discount</option>
                </select>
            </label>
            <p>
                <button className='finish'>
                    Complete order
                </button>
            </p>
            
        </div>
        
        
    );
}

export default EditOrder;
