import './App.css';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link, useLocation } from 'react-router-dom';
function EditOrder(){
    
    const location = useLocation();
    console.log('location', location.state);
    //const { selectedGroceries  = [] } = location.state || {}
    const selectedItems = location.state?.selectedItems || [];
    console.log('maybe items?', selectedItems);
    const [groceryDetails, setgroceryDetails] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    

    useEffect(() => {
        if(selectedItems && selectedItems.length > 0 ){
            if (loading) return;
            console.log('stuff', selectedItems);
            const getGroceryInfo = async() => {
                setLoading(true);
                try{
                    console.log('Selected groceries:', selectedItems);
                    const response = await axios.post('http://localhost:8080/api/EditOrder', {
                        selectedItems: selectedItems}, {
                        
                            headers: {
                                'Content-Type': 'application/json'
                            }
                        
                    });

                    console.log('server response', response);

                    if(Array.isArray(response.data)){
                        console.log('api response', response.data);
                        setgroceryDetails(response.data);
                    } else{
                        setError('Data not an array');
                        console.log(response.data);
                    }
                    
                } catch (err){
                    setError('Failed to fetch grocery data, try again');
                    console.error(err.message);
                } finally{
                    setLoading(false);
                }
            };
            getGroceryInfo();
        }
        
    }, [selectedItems]);

    console.log(error);

    if(loading) {
        return <div>Loading...</div>;
    }
    if(error){
        return <div>Error: {error.message}</div>;
    }

    console.log('groceries details', groceryDetails);


    return(
            <div>
           <ul>
           {/* {data && Array.isArray(data) && data.map((post, index) => (
                <li key={post.title}>{post.title}</li>
            ))} */}
           </ul>
           <p>
                Order Number:
           </p>
           <button><Link to='/ShopItems'>Add Item</Link></button>
           <table className='Orders'>
                <tr>
                    <th>Item</th>
                    <th>Unit cost</th>
                    <th>No. Units</th>
                    <th>Total Cost</th>
                    <th>Added by</th>
                </tr>
           </table>

           <br/>
           <table className='Orders'>
                <tr>
                    <th>User</th>
                    <th>Total Cost</th>
                </tr>
           </table>
           <p>
                Total cost:
           </p>
           <label>
                Apply discount
                <select className='discount'>
                    <option value="NoOp">Please Choose an available discount</option>
                </select>
            </label>
            <p>
                <button className='finish'>
                    Complete order
                </button>
            </p>
            
        </div>
        
        
    );
}

export default EditOrder;
